    function epl()
    {
        /** @var FilesystemProviderInterface */
        $storageProvider = $this->getContainer()->get(FilesystemProviderInterface::class);
        $publicDisk = $storageProvider->storage('public.storage');

        $magResponse = httpGet("https://exportsnews.com/api/magazines?limit=3");
        $magResponseContent = $magResponse->getBody()->getContents();

        /** @var Elasticsearch_Blogs_Model $elasticsearchBlogsModel */
        $elasticsearchBlogsModel = model(Elasticsearch_Blogs_Model::class);

        $data = [
            'magazines' => json_decode($magResponseContent, true),
            'blogs'     => $elasticsearchBlogsModel->get_blogs([
                'category'  => 3,
                'visible'   => 1,
                'published' => 1,
                'per_p'     => 3,
                'sort_by'   => ['publish_on-desc', 'date-desc']
            ])
        ];

        $data['blogs'] = array_map(function($blog) use ($publicDisk)
        {
            $blog['imagePath'] = $publicDisk->url(
                BlogsPathGenerator::publicImageBlogsPath($blog['id'], $blog['photo'])
            );
            return $blog;
        },
        $data['blogs']);

        views()->assign($data);
        views()->display('new/header_view');
        views()->display('new/landings/epl/index_view');
        views()->display('new/footer_view');
    }

