    public function giveaway()
    {
        try {
            // Get current date
            $currentDate = new DateTimeImmutable();
            // Get giveaway start date
            $startDate = Carbon::createFromFormat(DateTimeInterface::RFC3339, config('giveaway_start_datetime'))->timezone($currentDate->getTimezone());
            // Get giveaway start date
            $endDate = Carbon::createFromFormat(DateTimeInterface::RFC3339, config('giveaway_end_datetime'))->timezone($currentDate->getTimezone());
            $isStarted = $currentDate >= $startDate;
            $isEnded = $currentDate >= $endDate;
            $dateDiff = $isStarted && !$isEnded ? $endDate->diff($currentDate) : null;
        } catch (InvalidFormatException $e) {
            // If failed to parse datetime values than everything is empty
            $startDate = null;
            $endDate = null;
            $dateDiff = null;
            $isEnded = false;
            $isStarted = false;
        }

        views('new/landings/giveaway/index_view', [
            'icons'         =>  $this->icons(),
            'currentDate'   => $currentDate,
            'startDate'     => $startDate,
            'dateDiff'      => $dateDiff,
            'endDate'       => $endDate,
            'isEnded'       => !isBackstopEnabled() ? $isEnded : true,
            'isStarted'     => $isStarted,
            'webpackData'   => [
                'styleCritical' => 'giveaway',
                'pageConnect'   => 'giveaway_page',
            ],
            'templateViews' => [
                'mainOutContent'    => $isEnded && config('giveaway_show_winner_page') ? 'landings/giveaway/winner_page_content_view' : 'landings/giveaway/content_view',
                'customEncoreLinks' => true,
            ]
        ]);
    }

